--
-- 表的结构 `np_group`
--

CREATE TABLE IF NOT EXISTS `np_group` (
  `gid` int(11) NOT NULL AUTO_INCREMENT,
  `type` int(11) NOT NULL,
  `title` varchar(50) NOT NULL,
  `avatar` varchar(100) NOT NULL,
  `content` text NOT NULL,
  `order` int(11) NOT NULL,
  PRIMARY KEY (`gid`)
) ENGINE=MyISAM  DEFAULT CHARSET=utf8 AUTO_INCREMENT=67 ;

--
-- 转存表中的数据 `np_group`
--

INSERT INTO `np_group` (`gid`, `type`, `title`, `avatar`, `content`, `order`) VALUES
(10, 1, '崔利荣', '/upload/20121129/13541889248422.jpg', '<p style="text-indent:2em;">自1986年参加工作后，曾经在研究所、国企业、外企、国内大学、国外大学工作，并历任助理工程师、工程师、高级工程师、研究室总工程师、集团副总工程师。曾经以访问学者的身份访问过英国、台湾,在新加坡国立大学做过2年的Research&nbsp;Fellow。自2003年加入北京理工大学后，开始了高校的教学与科研工作。现发表论文100多篇，有三十多篇被SCI检索收录。承担国家自然基金三项(二项已结题)，教育部博士基金二项(一项已结题).多次在国际会议作为会议主席或程序委员会主席，筹办过第四届国际质量与可靠性会议（ICQR2005）、第七届国际可靠性数学、理论、方法与应用大会（MMR2011）。自2005年至今，担任IEEE&nbsp;Transactions&nbsp;on&nbsp;Reliability期刊的副主编，在多个可靠性有关的学术组织中担任要职。有广泛的学术合作。2005年获得教育部新世纪优秀人才支持计划。2009年获得北京市优秀博士论文指导教师证书。</p>', 11),
(20, 2, '', '/upload/20121130/13542423135356.jpg', '0', 0),
(21, 2, '', '/upload/20121130/1354242345612.jpg', '<p>欢迎使用ueditor!</p>', 0),
(58, 3, '李晓鹏', '/', '<div style="line-height:27px;">&nbsp;&nbsp;&nbsp;&nbsp;单位：中国工商银行总行&nbsp;&nbsp;&nbsp;&nbsp;职务：副行长<br />&nbsp;&nbsp;&nbsp;&nbsp;个人简介：1997年9月获得北京理工大学的工商管理硕士学位；<br />&nbsp;&nbsp;&nbsp;&nbsp;1979年9月至1984年11月，一直在人民银行工作。<br />&nbsp;&nbsp;&nbsp;&nbsp;1995年10月至1996年7月，担任工行河南省分行副行长。<br />&nbsp;&nbsp;&nbsp;&nbsp;1996年7月至1998年9月，担任工行营业部总经理；<br />&nbsp;&nbsp;&nbsp;&nbsp;1998年9月至1999年9月，担任工行四川省分行行长；<br />&nbsp;&nbsp;&nbsp;&nbsp;1999年10月至2002年12月担任华融公司副总裁；<br />&nbsp;&nbsp;&nbsp;&nbsp;2002年12月至2004年9月，担任工总行行长助理兼北京市分行行长。<br />&nbsp;&nbsp;&nbsp;&nbsp;2004年9月开始担任工总行副行长，主要负责投资银行业务、机构业务和企业年金业务相关事宜。<br /></div><p>&nbsp;</p>', 0),
(11, 1, '董沛武', '/upload/20121207/13548695165225.jpg', '<p style="text-indent:2em;">董沛武，男，1963年7月出生，博士生导师，教授。1984年7月，本科毕业于哈尔滨工业大学精密仪器制造工艺专业。1988年1月，研究生毕业于哈尔滨工业大学管理学院管理工程专业，毕业后留校任教。1989年7月评为讲师，1994年7月评为副教授，1996年任管理学院系统工程教研室副主任，2001年任管理学院管理科学与工程系副主任，2001年12月哈尔滨工业大学管理学院管理学博士毕业，2004年7月评为教授，2004年10月北京大学光华管理学院应用经济学博士后出站，2005年2月调入北京理工大学管理与经济学院，2006年7月任北京理工大学MBA教育中心主任，于2008年赴George&nbsp;Washington&nbsp;University访问，&nbsp;2010年赴Universidad&nbsp;Autónoma&nbsp;de&nbsp;Madrid，Universidad&nbsp;Politecnica&nbsp;de&nbsp;Valencia，Universitat&nbsp;de&nbsp;Barcelona，Politecnico&nbsp;di&nbsp;Torino访问。2010年1月任北京理工大学管理与经济学院副院长。</p><p style="text-indent:2em;">董沛武教授长期从事决策理论与方法、复杂系统建模与管理系统工程、投融资与风险管理、国防经济与安全预警方向的教学科研工作，有着深厚的研究基础和研究积累，有很强的科研能力和创新能力，是国家自然科学基金《产业与生态系统的耦合理论与实证研究——以林业产业为研究对象》项目负责人，国家型号基础研究《型号项目计划与控制管理系统方案设计》项目负责人，在《数量经济技术经济研究》、《中国软科学》、《宇航学报》等重要学术刊物和国际会议上发表高水平论文40余篇，其中EI、ISTP检索20余篇，出版专著4本，曾获航天部科技进步二等奖1项、航天部科技三等奖2项。并于2008年入选教育部“新世纪优秀人才”，2009&nbsp;年荣获“北京市优秀教师”称号，同年入选北京市教委“四个一批”人才。董沛武教授为学科发展做出了重要贡献，其研究成果和创新能力得到了同行专家的充分肯定，在国防型号项目管理与系统工程管理领域的科研成果居国内领先水平。</p><p style="text-indent:2em;">在科研与教学方面：</p><p style="text-indent:2em;">课题研究上，董沛武教授自1988年留校后，即开始从事国防领域的管理研究工作，二十余年坚持不懈，主持承担研究课题30余项。其中，作为第一负责人承担国家自然科学基金一项，作为第一负责人承担CAST创新基金一项，作为第一负责人承担国家国防基础、型号项目7项。其中研究项目《航天科技集团人才资源开发与信息化研究》为航天科技集团软科学重大研究项目。在国防领域从事的科研项目研究，总体上达到了国内先进水平。</p><p style="text-indent:2em;">论文发表上，董沛武教授在《中国软科学》、《科学学与科学技术管理》、《哈尔滨工业大学学报》、《北京理工大学学报》等核心及EI全文检索杂志上发表40余篇学术论文。2007年，研究了存款保险定价的量化方法，研究成果为我国将会推出的存款保险制度中存款保险费率的合理定价提供理论支持和政策设计建议；2008年，研究了基于VaR的股指期货风险测度问题，将其引入到我国股指期货的风险管理是一项极具意义的研究，并将所研究模型实证地应用于香港恒生指数期货的风险测度，据此研究成果发表的文章被EI全文检索。</p><p style="text-indent:2em;">教学上，董沛武教授任教二十余年来，先后为本科生、研究生讲授《国际营销学》、《系统工程》、《系统动态学》、《风险投资》、《生产运作管理》、《创业投资》等课程，授课学时2000余学时，教学效果良好。指导过本科、专科、本科双学位毕业论文，指导过本科生产实习，走过完整的教学环节。独立指导毕业硕士研究生100余人，协助指导毕业博士生1人。<br />教材建设上，董沛武教授参编教材《系统工程实用教程》一书，本人编写10万字，这本书在国内管理界有较大影响；副主编《企业经营管理的理论和实践》一书；编著了《航天系统工程管理专题研究》一书，中国宇航出版社出版发行。</p><p style="text-indent:2em;">在学科建设与管理工作方面：</p><p style="text-indent:2em;">学科建设上，董沛武教授承担了985工程重点学科建设&nbsp;“系统工程研究室”项目、211二期建设“信息化时代的国防系统工程”项目，参与了行政管理博士点研究方向“国防系统工程”申报及2003年教育部高校中长期发展规划项目中的“国防系统工程”内容。2005年，参与了博士点的申报工作，代学科负责人承担组织2005级“研究生入学师生互选工作”，代学科负责人承担组织2004级、2005级硕士中期检查，2005级开题审查，代学科负责人承担教学质量评估自评工作。2006年，负责北京理工大学MBA教育中心工作，提出并实施了MBA导师双向选择制度，修订了MBA培养方案。2007年，提出、起草并组织实施了MBA论文预评审制度，设计并试用MBA论文模版，组织承办了“北京理工大学2007MBA教学研讨会”。2008年，为提高MBA论文质量和学术水平，起草了“授予工商管理硕士（MBA）学位工作细则”，完善专业学位制度建设。2009年，负责国防经济硕士点学科建设工作。2010年，代表应用经济学科参加了学科评审的答辩汇报，总结介绍了学科特色和学科队伍。以教育部学科办官员为组长的评审组全票通过上报，为学科建设做出一份贡献。</p>', 3),
